mapper.select:
SELECT m.* FROM ( SELECT sn.* ,lmsg.mm_msg_type as mm_msg_type, lmsg.mm_msg_title as mm_msg_title, lmsg.mm_msg_content as mm_msg_content, lmsg.mm_msg_picurl as mm_msg_picurl, lmsg.provinceid as provinceid, lmsg.cityid as cityid, lmsg.dateline as datelineRecord, (SELECT mm_emp_mobile FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as mm_emp_mobile, (SELECT mm_emp_nickname FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as mm_emp_nickname , (SELECT mm_emp_type FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as mm_emp_type , (SELECT mm_emp_cover FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as mm_emp_cover , (SELECT mm_emp_company_type FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as mm_emp_company_type , (SELECT mm_emp_company FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as mm_emp_company , (SELECT mm_level_id FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as mm_level_id , (SELECT is_chengxin FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as is_chengxin , (SELECT is_miaomu FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as is_miaomu, (SELECT lat_company FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as lat, (SELECT lng_company FROM mm_emp lemp WHERE lemp.mm_emp_id = lmsg.mm_emp_id) as lng from mm_record_favour sn LEFT JOIN mm_msg lmsg ON sn.mm_msg_id=lmsg.mm_msg_id WHERE 1=1 AND lmsg.mm_msg_id is not NULL
AND sn.mm_emp_id = #{mm_emp_id}
) m ORDER BY m.dateline DESC

SELECT * FROM mm_record_favour where
mm_msg_id=#{mm_msg_id} AND
mm_emp_id=#{mm_emp_id}

SELECT count(sn.mm_emp_id) FROM mm_record_favour sn
WHERE 1=1

    AND sn.mm_emp_id = #{mm_emp_id}
